【老孟Flutter】源码分析系列之InheritedWidget
一文搞懂InheritedWidget局部刷新机制
flutter防止widget rebuild终极解决办法
重写了updated方法，调用InheritedWidget的setStatus时，不会对所有子widget调用build。而是对注册的监听调用。
class InheritedElement extends ProxyElement{
  @override
  //stylebegin {background-color: #FFFF0050;}
  void updated(InheritedWidget oldWidget) {
    if ((widget as InheritedWidget).updateShouldNotify(oldWidget)) {
      super.updated(oldWidget); --会调用notifyClients
    }
  }
  //styleend
  @override
  void notifyClients(InheritedWidget oldWidget) {
    assert(_debugCheckOwnerBuildTargetExists('notifyClients'));
    for (final Element dependent in _dependents.keys) {
      assert(() {
        // check that it really is our descendant
        Element? ancestor = dependent._parent;
        while (ancestor != this && ancestor != null) {
          ancestor = ancestor._parent;
        }
        return ancestor == this; 
      }());
      // check that it really depends on us
      assert(dependent._dependencies!.contains(this));
      notifyDependent(oldWidget, dependent); //遍历每一个监听者，调用其notifyDependent
    }
  }
}

abstract class Element extends DiagnosticableTree implements BuildContext {
    @protected
  void notifyDependent(covariant InheritedWidget oldWidget, Element dependent) {
    dependent.didChangeDependencies();
  }

    @mustCallSuper
  void didChangeDependencies() {
    assert(_lifecycleState == _ElementLifecycle.active); // otherwise markNeedsBuild is a no-op
    assert(_debugCheckOwnerBuildTargetExists('didChangeDependencies'));
    markNeedsBuild();
  }

  void markNeedsBuild() {
    if (dirty) {
      return;
    }
    _dirty = true;
    owner!.scheduleBuildFor(this);
  }
}


class BuildOwner {
void scheduleBuildFor(Element element) {
    _dirtyElements.add(element); //添加到dirtyElements列表中。
    element._inDirtyList = true; 
  }
}

每一个InheritedElement都会被存储到_inheritedWidgets字典中。
abstract class BuildContext {
    InheritedElement? getElementForInheritedWidgetOfExactType(); //抽象类声明

}


abstract class Element extends DiagnosticableTree implements BuildContext {

    PersistentHashMap? _inheritedWidgets; //每个Element都有这个

    @mustCallSuper
  void mount(Element? parent, Object? newSlot) { //mount和activate方法会调用_updateInheritance
    _updateInheritance();
  }

    @mustCallSuper
  void activate() {
    ...
    _updateInheritance();
  }

  void _updateInheritance() {
    _inheritedWidgets = _parent?._inheritedWidgets;
  }

    @override
  InheritedElement? getElementForInheritedWidgetOfExactType() {
    final InheritedElement? ancestor = _inheritedWidgets == null ? null : _inheritedWidgets![T];
    return ancestor;
  }
}

class InheritedElement extends ProxyElement{
  @override
  void _updateInheritance() {
    final PersistentHashMap incomingWidgets =
        _parent?._inheritedWidgets ?? const PersistentHashMap.empty();
    _inheritedWidgets = incomingWidgets.put(widget.runtimeType, this); //从父widget获取，并将自己保存起来。Widget的类型为key。element为value。
  }
}

abstract class Element extends DiagnosticableTree implements BuildContext
    @override
  T? dependOnInheritedWidgetOfExactType({Object? aspect}) {
    assert(_debugCheckStateIsActiveForAncestorLookup());
    final InheritedElement? ancestor = _inheritedWidgets == null ? null : _inheritedWidgets![T];
    if (ancestor != null) {
      return dependOnInheritedElement(ancestor, aspect: aspect) as T;
    }
    _hadUnsatisfiedDependencies = true;
    return null;
  }

      @override
  InheritedWidget dependOnInheritedElement(InheritedElement ancestor, { Object? aspect }) {
    //ancestor是 InheritedElement
    _dependencies ??= HashSet();
    _dependencies!.add(ancestor); //存储当前element依赖了哪些InheritedElement
    ancestor.updateDependencies(this, aspect); //添加到InheritedElement中。
    return ancestor.widget as InheritedWidget;
  }
}

class InheritedElement extends ProxyElement{

  final Map _dependents = HashMap();

        @protected
  void updateDependencies(Element dependent, Object? aspect) {
    setDependencies(dependent, null);
  }

    @protected
  void setDependencies(Element dependent, Object? value) {
    _dependents[dependent] = value;  //添加到对应的map种。 这里value为空。
  }
}


