abstract class Element extends DiagnosticableTree implements BuildContext

  Element? _parent;
  PersistentHashMap? _inheritedWidgets;
  Set? _dependencies;
  bool _hadUnsatisfiedDependencies = false;

    /// Add this element to the tree in the given slot of the given parent.
    @mustCallSuper
  void mount(Element? parent, Object? newSlot) {
    ...
    _updateInheritance();
    ...
  }

    /// Transition from the "inactive" to the "active" lifecycle state.
    @mustCallSuper
  void activate() {
    ...
    _updateInheritance();
        ...
  }

  void _updateInheritance() {
        ...
    _inheritedWidgets = _parent?._inheritedWidgets;
  }
}

class InheritedElement extends ProxyElement{

  final Map _dependents = HashMap();

  @override
  void _updateInheritance() {
    final PersistentHashMap incomingWidgets =
        _parent?._inheritedWidgets ?? const PersistentHashMap.empty();
    _inheritedWidgets = incomingWidgets.put(widget.runtimeType, this);
  }
}

