class InheritedProvider extends SingleChildStatelessWidget {

  // 1. 构造方法
  InheritedProvider({
    Key? key,
    Create? create,   // 用来创建Provider持有的对象
    T Function(BuildContext context, T? value)? update, // 当前Widget更新时触发。
    UpdateShouldNotify? updateShouldNotify,  // 观察者是否触发内容。
    // 第一次获取数据的时候被调用。返回一个取消订阅的函数
    StartListening? startListening, 
    Dispose? dispose, //unmount的时候的回调函数
    this.builder, //构造语法糖
    bool? lazy, // create方法是否懒加载
    Widget? child,
  })  : _lazy = lazy,
        _delegate = _CreateInheritedProvider(
          create: create,
          update: update,
          updateShouldNotify: updateShouldNotify,
          debugCheckInvalidValueType: debugCheckInvalidValueType,
          startListening: startListening,
          dispose: dispose,
        ),
        super(key: key, child: child);

  final _Delegate _delegate;
  final bool? _lazy;

  final TransitionBuilder? builder;

  @override
  _InheritedProviderElement createElement() {
    return _InheritedProviderElement(this);
  }

  @override
  Widget buildWithChild(BuildContext context, Widget? child) {
    return _InheritedProviderScope(
      owner: this,
      debugType: kDebugMode ? '$runtimeType' : '',
      child: builder != null
          ? Builder(
              builder: (context) => builder!(context, child),
            )
          : child!,
    );
  }
}
